(								  get_folder_data	application=waterquality				  			folder=results				  			select="station,collection_date,collection_time,parameter,units,'aaaa',concentration"				  			where=" station = 'EP' and parameter = 'DISSOLVED OXYGEN' and units = 'mg/L' and collection_date >= '2007-04-17' and collection_date <= '2007-12-11'"				 |tr '^' ','							 |sort -r								 |real_time2aggregate_value.e average 3 -1 5 ',' weekly n 2007-12-11								 |sort								 ; get_folder_data	application=waterquality		    			folder=results		    			select="station,collection_date,collection_time,parameter,units,'zzzz',concentration"		    			where=" station = 'CR2' and parameter = 'DISSOLVED OXYGEN' and units = 'mg/L' and collection_date >= '2007-04-17' and collection_date <= '2007-12-11'"		   |tr '^' ',' 					   |sort -r	 					   |real_time2aggregate_value.e average 3 -1 5 ',' weekly n 2007-12-11	 					   |sort						    ) | sort --key=4,5 --field-separator=',' |piece_inverse.e 4 ','							|search_replace_character_limited.e ',' '/' 2		|sed 's|\(/[0-9][0-9]\)\([0-9][0-9]\),|\100,|'							|covariance.e 'EP/DISSOLVED OXYGEN mg/L' 1 0 2 ','			|cat
